document.addEventListener("DOMContentLoaded",(function(){const e=JSON.parse(localStorage.getItem("students"))||[],t=document.getElementById("student-form");t&&t.addEventListener("submit",(function(n){n.preventDefault();const d={firstName:document.getElementById("first-name").value,lastName:document.getElementById("last-name").value,course:document.getElementById("course").value,grade:document.getElementById("grade").value};e.push(d),localStorage.setItem("students",JSON.stringify(e)),alert("Student added successfully!"),t.reset(),window.location.href="index.html"}));const n=document.getElementById("student-table");n&&e.forEach((e=>{const t=document.createElement("tr");t.innerHTML=`\n                <td>${e.firstName}</td>\n                <td>${e.lastName}</td>\n                <td>${e.course}</td>\n                <td>${e.grade}</td>\n            `,n.appendChild(t)}))}));